export const SET_GAMES = 'SET_GAMES';

export function setGames(games){
	return{
		type: SET_GAMES,
		games
	}
}
export function fetchGames(){
			{/* step 4 - this function is called , it fetches the API and */}
	return dispatch => {
		fetch("http://localhost:3001/api/v1/interviews")
			.then(res => res.json())
			.then(data => dispatch(setGames(data.data)));
	}
}
