export const SET_GAMES = 'SET_GAMES';

export function setGames(games){
	return{
		type: SET_GAMES,
		games
	}
}

export function saveGame(data){
	return dispatch => {
		return fetch(`http://localhost:3001/api/v1/interviews`, {
            method: 'POST',
            body: JSON.stringify(data),
            mode: 'cors',
            headers: {
                'Content-Type': 'application/json'
            }
		});
	}
}
export function fetchGames(){
			{/* step 4 - this function is called , it fetches the API and dispateches data to Reducer in games.js */}
	return dispatch => {
		fetch("http://localhost:3001/api/v1/interviews")
			.then(res => res.json())
			.then(data => dispatch(setGames(data.data)));
	}
}
