import React, {useEffect, useState} from 'react';
import './App.css';
import {Link} from  'react-router-dom';
import {connect} from 'react-redux';
import GamesList from './GamesList'

import PropTypes from 'prop-types';

class GamesPage extends React.Component{
  render(){
  return (
    <div>
     	<h1>
     		Games List
     		<GamesList games={this.props.games} />
     				{/* step 5 - Calling GameList.js and passing local variable games*/}
     	</h1>
    </div>
  );
}
}


GamesPage.propType ={
	games: PropTypes.array.isRequired
}
// } 		{ step 4 - Gives shape of array to local games variable }

function mapStateToProps(state){
	{/* step 3 - assign state(recived from reducer to local variable */}
	return{
		games: state.games
	}
}


export default connect(mapStateToProps)(GamesPage);
{/* step 2 this statement combines Redux(mapStateToProps) to GamesPage function */}
