import React from 'react';
import PropTypes from 'prop-types';
import {Link} from  'react-router-dom';

// import GameCard from './GameCard';

function GamesList({ games }){
	// console.log(games[0]);
			{/* step 9 : this funtion is called from GamesPage Class and games contain the data*/}
	const emptyMessage = (
			<p> There are no games yet in you collection </p>
		);
	const gamesList = (
		<div>
			{games.map(item => (
	     		<h1 key={item.id}>
	          <Link to={`/interviews/${item.id}`}>{item.id} {item.topic}</Link>
	        </h1>
	     	))}
		</div>
	)
		 		{/* step 6 - declaring two variables */}
	return (
		<div>
			{games.length == 0 ? emptyMessage : gamesList}
					{/* applying ternary operator depending on condition */}
		</div>
	);

}


GamesList.propTypes = {
	games: PropTypes.array.isRequired
}

export default GamesList
