import {SET_GAMES} from '../actions';
		{/* step 5 : this function return response to mapStateToProps in GamesPage.js*/}
function games(state = [], action ={}){
	switch(action.type){
		case SET_GAMES:
			return action.games;
		default: return state;
	}
}

export default games;
